mapper.select:
SELECT t.transaction_id , t.client_id , c.name , c.business_number, DATE_FORMAT(t.transaction_date, '%Y-%m-%d') AS transaction_date, t.transaction_amount, DATE_FORMAT(t.expected_payment_date ,'%Y-%m-%d') AS expected_payment_date, DATE_FORMAT(t.recovered_date ,'%Y-%m-%d') AS recovered_date, t.recovered_amount, t.created_at, t.updated_at from `transaction` t INNER JOIN client c ON t.client_id = c.client_id
WHERE t.client_id = #{condition.clientId}
ORDER BY transaction_date desc
